sp_widget.client_script:
function($scope,$rootScope,spModal,spUtil) {
	/* widget controller */
	var c = this;
	//$scope.editFlag = false;
	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
			c.data.pq = r.data.note.priority;
			c.data.due = r.data.note.due;
			//$scope.editFlag = true;
		});
	});
c.backToList = function() {
	c.data.noteID = '';
	$rootScope.$emit('afterEdit', true); 
}

	c.updateNote = function(updateType) {
		console.log("at note eidt client ,changing: cur pq is: \n");
			console.log(c.data.pq);
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title,
			notePQ : c.data.pq,
			noteDue: c.data.due
		}).then(function(r) {
			// When the promise is returned, emit the change
			if (updateType == 'title' || updateType == 'body' || updateType == 'pq' || updateType == 'due') { 
				console.log("at note eidt client , new cur due is: \n");
				console.log(r.data.due);
				$rootScope.$emit('updateTitle', r.data); 
			}
		});
	}

	c.deleteNote = function() {
		c.server.get({
			action: 'deleteNote',
			noteID: c.data.noteID
		}).then(function(r) {
			spUtil.addTrivialMessage("The " + c.data.title + " record has been deleted.");
			$rootScope.$emit('deleteNote', c.data.noteID);
			c.data.title = '';
			c.data.note = '';
			c.data.noteID = '';
			//$scope.editFlag = false;
		});
	}

	c.confirmDelete = function(){
		spModal.confirm("Are you sure you want to delete this Note record?").then(function(confirmed){
			if(confirmed){
				c.deleteNote();
			}
		});
	}

}

sp_widget.script:
(function() {

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, due, priority, sys_id");
			}





			else if (input.action == 'updateNote') { 
				note.title = input.noteTitle; 
				note.note = input.noteBody; 
				note.priority = input.notePQ;
				note.due = input.noteDue;
				console.log("at note eidt server,recevied cur due is: \n");
			  console.log(input.noteDue);
				note.update(); 

				// Need to update the data object with the new title
				// and the note
				data.title = note.getValue('title');
				data.note = note.getValue('note');
				data.priority = note.getValue('priority');
				data.due = note.getValue('due');
				console.log("at note eidt server , new cur due is: \n");
			console.log(data.due);
			}

			else if (input.action == 'deleteNote') { 
				note.deleteRecord(); 
				var own = new GlideRecord('x_snc_createnotes_own');
				if (own.get('noteid', input.noteID)) {
					console.log("delete own record success\n");
					own.deleteRecord();
				}
			}
		}
	}
})();
