sp_widget.client_script:
function($scope,$rootScope, spUtil) {
	/* widget controller */
	var c = this;

	//$rootScope.loginFlag = true;
	$scope.loginOkay = true;
	$scope.registerOkay = false;
	$scope.pwdNotMatch = false;
	$scope.duplicatedEmail = false;

	c.login = function() {
		//var id = c.data.notes[idx].sys_id;
		//console.log('Note email: ' + c.data.userEmail);
		//console.log('Note pwd: ' + c.data.userPwd);

		//var logInData = {email : c.data.userEmail, pwd : c.data.userPwd, loginFlag : $scope.loginOkay};

		var logInData = {email : c.data.userEmail, pwd : c.data.userPwd};

		$rootScope.$emit('loginNote', logInData);


		//$scope.loginOkay = $rootScope.loginFlag;
	}

	$rootScope.$on('acLogin', function(event,data) { 
		//console.log("after log in ac received: \n");
		if (data) {
			$scope.loginOkay = false;
		} else {
			spUtil.addErrorMessage("The input email or password are not correct!!!");
		}
		//$scope.loginOkay = data;
		//console.log($scope.loginOkay);
	});

	c.register = function() {
		$scope.loginOkay = false;
		$scope.registerOkay = true;
	}
	c.checkEmail = function() {
		$scope.duplicatedEmail = false;
	}
	c.checkPwdEqual = function() {
		$scope.pwdNotMatch = c.data.regPwd != c.data.regPwdAgain;
	}

	c.createUser = function() {
		if (!$scope.pwdNotMatch) {
			//Two pwds match to each other.
			//Interaction with db(user table) to create a new record for this new user.
			c.server.get({
				action: 'newUser',
				regEmail: c.data.regEmail,
				regPwd : c.data.regPwd
			}).then(function(r) {
				if (!r.data.isExistent) {
					spUtil.addInfoMessage("Registration succeeds!!!");
					c.cancel();
				} else {
					$scope.duplicatedEmail = true;
				}
			});
		}
	}


	c.cancel = function() {
		$scope.loginOkay = true;
		$scope.registerOkay = false;
		c.data.userEmail = '';
		c.data.userPwd = '';
	}




	$rootScope.$on('logoutNote', function(event,data) { 
		/*console.log("after log out: \n");
		console.log(event);
		console.log(data);
		*/
		c.data.userEmail = '';
		c.data.userPwd = '';
		$scope.loginOkay = data;
	});







}

