sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	//$rootScope.loginFlag = true;
	$scope.loginOkay = true;
	c.login = function() {
		//var id = c.data.notes[idx].sys_id;
		//console.log('Note email: ' + c.data.userEmail);
		//console.log('Note pwd: ' + c.data.userPwd);

		var logInData = {email : c.data.userEmail, pwd : c.data.userPwd, loginFlag : $scope.loginOkay};

		$rootScope.$emit('loginNote', logInData);

		//$scope.loginOkay = $rootScope.loginFlag;
	}

	$rootScope.$on('acLogin', function(event,data) { 
		//console.log("after log in ac received: \n");

		$scope.loginOkay = data;
		//console.log($scope.loginOkay);
	});

	$rootScope.$on('logoutNote', function(event,data) { 
		/*console.log("after log out: \n");
		console.log(event);
		console.log(data);
		*/
		c.data.userEmail = '';
		c.data.userPwd = '';
		$scope.loginOkay = data;
	});







}
